import * as React from 'react'
import axios from 'axios';
import styled from 'styled-components';
import {Button, Select, MenuItem, Box, Modal, Typography} from '@mui/material';
import {Container, Header, HeaderItem1, HeaderItem2, GraphContainer, HoldingsContainer} from '../../styles/PortfolioStyles'

const style = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: 200,
  bgcolor: 'background.paper',
  border: '2px solid #000',
  boxShadow: 24,
  p: 4,
"overflow-y": 'auto'
};


const api = axios.create({baseURL: 'https://api.coinstats.app/public/v1/coins?skip=0&limit=100&currency=US'})
function PortTracker() {
  const [coins, setCoins] = React.useState([])
  const [selectCoin, setSelectCoin] = React.useState("Select")

  const [open, setOpen] = React.useState(false);
  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);


// const handleClick = () => {
//     api.get()
//     .then((res) =>{ 
   
//     setCoins(res.data.coins)})
//     .catch((err) => console.log(err))
// }

React.useEffect(() => {

  api.get()
  .then((res) => {
      console.log(res)
      setCoins(res.data.coins)
  })
  .catch(err => console.log(err))
}, [])

const handleChange = (res) => {
  console.log(res)
  setSelectCoin(res)
}


  return (
    <Container>
        <h1>PortTracker</h1>
    {/* <Button variant="contained" style={{width: "200px"}} onClick={handleClick} >Add New Coin</Button> */}
    {
    coins.length > 0 ? 
    <Select
    id="demo-simple-select"
    value={selectCoin}
    onChange={(e) => handleChange(e.target.value)}
    
    label={"List Coins"}
  >
    <MenuItem value={"Select"} key={0}>{"Select"}</MenuItem>
    {
        
      coins.map((item) => {
        return(
        
        <MenuItem value={item.id} key={item.id}>{item.name}</MenuItem>
        
        )
        
      })
      }
      
  
  </Select>
  :
  null
  }

<Button onClick={handleOpen} variant={"contained"}>Open modal</Button>
<Modal
  open={open}
  onClose={handleClose}
  aria-labelledby="modal-modal-title"
  aria-describedby="modal-modal-description"
>
  <Box sx={style}>
    {coins.map((item) => {
      return (
        <Typography id="modal-modal-title" variant="h6" component="h2">
          {item.name}
        </Typography>
      )
    })}
    

    {/* <Typography id="modal-modal-description" sx={{ mt: 2 }}>
      Duis mollis, est non commodo luctus, nisi erat porttitor ligula.
    </Typography> */}
  </Box>
</Modal>
    <Header>
      <HeaderItem1>
        <p>Current Balance</p>
        <h1>2,000</h1>
      </HeaderItem1>
      <HeaderItem2>
        <Button variant="contained">More</Button>
        <Button variant="contained">Add New</Button>
      </HeaderItem2>
     
    </Header>

    <GraphContainer>

    </GraphContainer>
    
    <HoldingsContainer>

    </HoldingsContainer>
    </Container>
  )
}

export default PortTracker
