import * as React from 'react'
import axios from 'axios';
import styled from 'styled-components';
import {Button, Select, MenuItem, Box, Modal} from '@mui/material';
import {Container, Header, HeaderItem1, HeaderItem2, GraphContainer, HoldingsContainer} from '../../styles/PortfolioStyles'
import NestedModal from './NestedModal';


const style = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: 300,
  height: 500,
  bgcolor: 'background.paper',
  border: '2px solid #000',
  boxShadow: 24,
  p: 4,
  overflow: 'auto',
  display: "flex",
  flexDirection: "column"
};



const api = axios.create({baseURL: 'https://api.coinstats.app/public/v1/coins?skip=0&limit=100&currency=US'})
function PortTracker() {
  const [coins, setCoins] = React.useState([])
  const [selectedCoin, setSelectCoin] = React.useState("Select")

  const [open, setOpen] = React.useState(false);
  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);


// const handleClick = () => {
//     api.get()
//     .then((res) =>{ 
   
//     setCoins(res.data.coins)})
//     .catch((err) => console.log(err))
// }

React.useEffect(() => {

  api.get()
  .then((res) => {
      console.log(res)
      setCoins(res.data.coins)
  })
  .catch(err => console.log(err))
}, [])

const handleChange = (res) => {
  console.log(res)
  setSelectCoin(res)
}


  return (
    <Container>
        <h1>PortTracker</h1>
    {/* <Button variant="contained" style={{width: "200px"}} onClick={handleClick} >Add New Coin</Button> */}
    

<Button onClick={handleOpen} variant={"contained"}>Open modal</Button>
<Modal
  open={open}
  onClose={handleClose}
  aria-labelledby="modal-modal-title"
  aria-describedby="modal-modal-description"
>
  <Box sx={style}>
    {coins.map((item) => {
      return (
          
          <NestedModal style={style} item={item}>
            {/* <Button 
            value={item.id} 
            sx={{justifyContent: "space-between", width: "100%", height: "100%"}}
            onClick={(e) => handleChange(e.target.value)}
            >
              {item.name}
              <img src={`${item.icon}`} sx={{height: "15px", width: "15px", position: 'absolute'}}/>
            </Button> */}
          </NestedModal>
        

       
      )
    })}
    

    {/* <Typography id="modal-modal-description" sx={{ mt: 2 }}>
      Duis mollis, est non commodo luctus, nisi erat porttitor ligula.
    </Typography> */}
  </Box>
</Modal>
    <Header>
      <HeaderItem1>
        <p>Current Balance</p>
        <h1>2,000</h1>
      </HeaderItem1>
      <HeaderItem2>
        <Button variant="contained">More</Button>
        <Button variant="contained">Add New</Button>
      </HeaderItem2>
     
    </Header>

    <GraphContainer>

    </GraphContainer>
    
    <HoldingsContainer>

    </HoldingsContainer>
    </Container>
  )
}

export default PortTracker
