import './App.css';
import React from 'react';
import CollapsibleTable from './components/CryptoTableList.jsx';
import axios from 'axios';
import styled from 'styled-components';
import { useSpring, animated, config } from '@react-spring/web'
import styles from './styles.css'



const Container = styled.div`
display: flex;
flex-direction: column;
align-items: center;
`

const Header = styled.div`
margin-top: 5rem;
height: 20vh;
align-items: center;
justify-content: center;
font-size: 5rem;
`

const api = axios.create({baseURL: 'https://api.cryptostats.community/api/v1/fees/'})

function App() {

  const [{ background }] = useSpring(
    () => ({
      from: { background: 'var(--step0)' },
      to: [
        { background: 'var(--step0)' },
        { background: 'var(--step1)' },
        { background: 'var(--step2)' },
        { background: 'var(--step3)' },
        { background: 'var(--step4)' },
      ],
      config: config.molasses,
      loop: {
        reverse: true,
      },
    }),
    []
  )

  const [data, setData] = React.useState([])

  const fetchApi = async () => {
    await api.get('oneDayTotalFees/2022-12-08', null,
    )
    .then((res) => {
        const array = res.data.data
        array.sort((a, b) => {
          return (b.results.oneDayTotalFees - a.results.oneDayTotalFees) 
        })
        console.log(array)
        setData(res.data.data)
    })
    .catch((err) => {
        console.log(err)
    })
}

  React.useEffect(() => {

    fetchApi();
  }, [])


  return (
    <Container className={styles.container}>
      <Header>
        Crypto Fees
      </Header>
      { data && <CollapsibleTable data={data}/>}

    </Container>
  );
}

export default App;
