import React from 'react';
import KeyboardArrowDownIcon from '@mui/icons-material/KeyboardArrowDown';
import KeyboardArrowUpIcon from '@mui/icons-material/KeyboardArrowUp';
import {Box, Collapse, IconButton, TableCell, TableRow} from '@mui/material';
import styled from 'styled-components';


const DropDownSection = styled.div`
margin: .5rem;
width: 100%;
/* flex-direction: column; */
`

const DropDownInfoSection = styled.div`
width: 80%;
display: flex;
flex-direction: row;
flex-wrap:wrap;
justify-content: space-between;
`

const InfoSectionItem = styled.div`
width: 30%;
margin: .5rem;
`

function Row({data:{metadata, results}}) {
    const [open, setOpen] = React.useState(false);
    const toUpperCase = (string) => {
      const newString = string[0].toUpperCase() + string.substring(1)
      return newString
    }



  
    return (
      <React.Fragment>
        <TableRow sx={{ '& > *': { borderBottom: 'unset' } }}>
          <TableCell sx={{width:'10%'}}>
            <IconButton
              aria-label="expand row"
              size="small"
              onClick={() => setOpen(!open)}
            >
              {open ? <KeyboardArrowUpIcon /> : <KeyboardArrowDownIcon />}
            </IconButton>
          </TableCell>
          <TableCell align="right" sx={{width: '15%'}}>{metadata.tokenTicker}</TableCell>
          <TableCell align="right" sx={{width: '30%'}}>{metadata.name}</TableCell>
          <TableCell align="right" sx={{width: '30%'}}>${(Math.round(results.oneDayTotalFees * 100)/100).toLocaleString("en-US")}</TableCell>
        </TableRow>
        <TableRow>
          <TableCell style={{ paddingBottom: 0, paddingTop: 0}} colSpan={6}>
            <Collapse in={open} timeout="auto" unmountOnExit>
              <Box sx={{ margin: 1, width:'100%',  display: 'flex',flexDirection:'column'}}>
                  <DropDownSection>{metadata.description}
                  </DropDownSection>
                  <DropDownSection>Fee Model: <br/>
                    {metadata.feeDescription}
                  </DropDownSection>
               
                 <DropDownInfoSection>
                  <InfoSectionItem >Website:  <br/> <a href={metadata.website}>{metadata.website}</a></InfoSectionItem>
                  <InfoSectionItem >Blockchain:  <br/>{metadata.blockchain}</InfoSectionItem>
                  <InfoSectionItem >Category:  <br/> {toUpperCase(metadata.category)}</InfoSectionItem>
                 </DropDownInfoSection>
              </Box>
            </Collapse>
          </TableCell>
        </TableRow>
      </React.Fragment>
    );
  }
  

  export default Row;
