import './App.css';
import React from 'react';
import CollapsibleTable from './components/CryptoTableList.jsx';
import axios from 'axios';
import styled from 'styled-components';
// @import url('https://fonts.googleapis.com/css?family=Nova Flat');




const Container = styled.div`
display: flex;
flex-direction: column;
align-items: center;
background: blue;
animation: myAnimation 5s linear infinite;
background-repeat: repeat-x;

  /* @keyframes myAnimation {
    0% {
      background-position: 0 0;
    }
    100% {
      background-position: -200px 0;
    }
  } */
`

const Header = styled.div`
margin-top: 5rem;
height: 20vh;
align-items: center;
justify-content: center;
font-size: 5rem;
font-family: n;
`

const api = axios.create({baseURL: 'https://api.cryptostats.community/api/v1/fees/'})

function App() {


  const [data, setData] = React.useState([])

  const fetchApi = async () => {
    await api.get('oneDayTotalFees/2022-12-08', null,
    )
    .then((res) => {
        const array = res.data.data
        array.sort((a, b) => {
          return (b.results.oneDayTotalFees - a.results.oneDayTotalFees) 
        })
        console.log(array)
        setData(array)
    })
    .catch((err) => {
        console.log(err)
    })
}

  React.useEffect(() => {

    fetchApi();
  }, [])


  return (
    <Container >
      <Header>
        Crypto Fees
      </Header>
      { data && <CollapsibleTable data={data}/>}

    </Container>
  );
}

export default App;
