import React from 'react'
import axios from 'axios';
import styled from 'styled-components';
import {Button, Select, MenuItem} from '@mui/material';
import {Container, Header, HeaderItem1, HeaderItem2, GraphContainer, HoldingsContainer} from '../../styles/PortfolioStyles'

const api = axios.create({baseURL: 'https://api.coinstats.app/public/v1/coins?skip=0&limit=100&currency=US'})
function PortTracker() {
  const [coins, setCoins] = React.useState([])
  const [selectCoin, setSelectCoin] = React.useState("")
console.log(coins.length)

const handleClick = () => {
    api.get()
    .then((res) =>{ 
    console.log(res)
    setCoins(res.data.coins)})
    .catch((err) => console.log(err))
}

const handleChange = (res) => {
  console.log(res)
  setSelectCoin(res)
}


  return (
    <Container>
        <h1>PortTracker</h1>
    <Button variant="contained" style={{width: "200px"}} onClick={handleClick} >Get coins</Button>
    {
    coins.length > 0 ? 
    <Select
    id="demo-simple-select"
    value={selectCoin}
    onChange={(e) => handleChange(e.target.value)}
    
    label={"List Coins"}
  >
    {
      coins.map((item) => {
        return(<MenuItem value={item.id}>{item.name}</MenuItem>)
        
      })
      }
      
  
  </Select>
  :
  <p>Please Get Coins First</p>
  }
    <Header>
      <HeaderItem1>
        <p>Current Balance</p>
        <h1>2,000</h1>
      </HeaderItem1>
      <HeaderItem2>
        <Button variant="contained">More</Button>
        <Button variant="contained">Add New</Button>
      </HeaderItem2>
     
    </Header>

    <GraphContainer>

    </GraphContainer>
    
    <HoldingsContainer>

    </HoldingsContainer>
    </Container>
  )
}

export default PortTracker
