import React from 'react';
import KeyboardArrowDownIcon from '@mui/icons-material/KeyboardArrowDown';
import KeyboardArrowUpIcon from '@mui/icons-material/KeyboardArrowUp';
import {Box, Collapse, IconButton, TableCell, TableRow} from '@mui/material';
import styled from 'styled-components';


const DropDown = styled.div`
margin: 1remm;
width: 80%;
flex-direction: column;
`

function Row({data:{metadata, results}}) {
    const [open, setOpen] = React.useState(false);
  
  
    return (
      <React.Fragment>
        <TableRow sx={{ '& > *': { borderBottom: 'unset' } }}>
          <TableCell sx={{width:'10%'}}>
            <IconButton
              aria-label="expand row"
              size="small"
              onClick={() => setOpen(!open)}
            >
              {open ? <KeyboardArrowUpIcon /> : <KeyboardArrowDownIcon />}
            </IconButton>
          </TableCell>
          <TableCell align="right" sx={{width: '15%'}}>{metadata.tokenTicker}</TableCell>
          <TableCell align="right" sx={{width: '30%'}}>{metadata.name}</TableCell>
          <TableCell align="right" sx={{width: '30%'}}>${(Math.round(results.oneDayTotalFees * 100)/100).toLocaleString("en-US")}</TableCell>
        </TableRow>
        <TableRow>
          <TableCell style={{ paddingBottom: 0, paddingTop: 0}} colSpan={6}>
            <Collapse in={open} timeout="auto" unmountOnExit>
              <Box sx={{ margin: 1, width:'100%',  display: 'flex',flexDirection:'column'}}>
                  <DropDown>
                    {metadata.description}
                    Fee Model: <br/>
                    {metadata.feeDescription}
                    Website:  <br/> {metadata.website}
                 </DropDown>
              </Box>
            </Collapse>
          </TableCell>
        </TableRow>
      </React.Fragment>
    );
  }
  

  export default Row;
